import React, { useState, useRef } from 'react';
import { NavLink } from 'react-router-dom';
import InputField from '../../common/InputField/InputField';
import {
  isFieldEmpty,
  isEmailInvalid,
  isPasswordValid,
} from '../../../utils/validators';
import {
  isFormValid,
  handleInputChange,
  handleInputBlur,
} from '../../../utils/form-validation';

const NewDepartment = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
  });

  const [alert, setAlert] = useState(null);
  const [formWasValidated, setFormWasValidated] = useState(false);

  const references = {
    email: useRef(),
    password: useRef(),
  };

  const formErrorTypes = Object.freeze({
    required: `Please fill me in`,
    passwordLength: `The passwords needs to be at least 8 characters long`,
    validEmail: `E-mail is not the right format`,
  });

  const [formErrors, setFormErrors] = useState({
    email: '',
    password: '',
  });

  const messageTypes = Object.freeze({
    failCaptcha: `Please prove me that you're not a robot ü§ñ`,
  });

  const validators = {
    email: {
      required: isFieldEmpty,
      validEmail: isEmailInvalid,
    },
    password: {
      required: isFieldEmpty,
      passwordLength: isPasswordValid,
    },
  };

  return (
    <div className='container-sm mt-5'>
      <h1>√öj szervezeti egys√©g hozz√°ad√°sa</h1>
      <NavLink
        className='navbar-item nav-link'
        exact
        to='/departments'
        activeClassName='activeClicked'
      >
        <button type='button' class='btn btn-dark mt-5'>
          Vissza
        </button>{' '}
      </NavLink>
      <form
        noValidate
        // onSubmit={handleLogin}
        className={`needs-validation ${formWasValidated && 'was-validated'}`}
      >
        <InputField
          name='password'
          value={formData.password}
          labelText='Password *'
          onChange={e => {
            handleInputChange(e, formData, setFormData);
          }}
          onBlur={e => {
            handleInputBlur(
              e,
              formData,
              setFormErrors,
              validators,
              references,
              formErrorTypes
            );
          }}
          reference={references.password}
          error={formErrors.password}
          required
        />
      </form>
    </div>
  );
};

export default NewDepartment;
