const InputFieldSet = ({
  name,
  type,
  placeholder,
  error,
  formData,
  onChange,
  onBlur,
  labelText,
  required,
  reference,
  employeeOptions,
}) => {
  let inputField;
  if (type === 'select') {
    inputField = (
      <select
        id={name}
        name={name}
        required={required}
        className={'form-select'}
        onChange={onChange}
        onBlur={onBlur}
        ref={reference}
        value={formData}
      >
        <option value={''}>VÃ¡lassz!</option>
        {employeeOptions.map(option => (
          <option value={option} key={option.familyName}>
            {option.familyName} {option.givenName}
          </option>
        ))}
      </select>
    );
  } else if (type === 'textarea') {
    inputField = (
      <textarea
        className='form-control'
        id={name}
        name={name}
        onChange={onChange}
        onBlur={onBlur}
        required={required}
        ref={reference}
        rows='3'
        value={formData}
      />
    );
  } else {
    inputField = (
      <input
        type={type}
        placeholder={placeholder}
        className='form-control'
        id={name}
        name={name}
        value={formData}
        onChange={onChange}
        onBlur={onBlur}
        required={required}
        ref={reference}
      />
    );
  }

  return (
    <div className={`mb-3 ${error !== '' ? 'was-validated' : ''}`}>
      <label htmlFor={name} className='form-label'>
        {labelText}
      </label>
      {inputField}
      <div className='invalid-feedback'>{error}</div>
    </div>
  );
};

export default InputFieldSet;
