import React, { useState, useEffect } from 'react';
import { Routes, Route } from 'react-router-dom';
import Navbar from './components/common/Navbar/Navbar';
import Departments from './components/pages/Departments/Departments';
import Employees from './components/pages/Employees/Employees';
import Datalist from './components/pages/Datalist/Datalist';
import NewDepartment from './components/pages/NewDepartment/NewDepartment';
import NewEmployee from './components/pages/NewEmployee/NewEmployee';
import EditEmployee from './components/pages/NewEmployee/NewEmployee';
import './App.scss';

const App = () => {
  const [departments, setDepartments] = useState([
    'pénzügyi osztály',
    'személyügy',
    'igazgatóság',
    'szakszervezet',
  ]);

  useEffect(() => {
    try {
      const data = localStorage.getItem('employees');

      if (data) {
        setEmployees(JSON.parse(data));
      } else {
        const dummy = {
          employeeId: '',
          familyName: '',
          givenName: '',
          birthdate: '',
          daysOffLimit: '',
          daysOffRemained: '',
          department: '',
        };
        setEmployees(JSON.parse(dummy));
      }
    } catch (error) {
      console.error(error);
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('employees', JSON.stringify(employees));
  });

  const [employees, setEmployees] = useState('');

  console.log('employees', typeof employees);
  console.log('employees', employees);

  return (
    <div className='App'>
      <Navbar />
      <Routes>
        <Route
          path='departments'
          element={<Departments departments={departments} />}
        />
        <Route path='newDepartment' element={<NewDepartment />} />
        <Route path='employees' element={<Employees employees={employees} />} />
        <Route
          path='employees/new'
          element={
            <NewEmployee
              departments={departments}
              employees={employees}
              setEmployees={setEmployees}
            />
          }
        />
        <Route
          path='employees/edit'
          element={
            <EditEmployee
              departments={departments}
              employees={employees}
              setEmployees={setEmployees}
            />
          }
        />
        <Route path='datalist' element={<Datalist />} />
      </Routes>
    </div>
  );
};

export default App;
