import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route, Link } from 'react-router-dom';
import Navbar from './components/common/Navbar/Navbar';
import Departments from './components/pages/Departments/Departments';
import Employees from './components/pages/Employees/Employees';
import Datalist from './components/pages/Datalist/Datalist';
import NewDepartment from './components/pages/NewDepartment/NewDepartment';
import NewEmployee from './components/pages/NewEmployee/NewEmployee';
import EditEmployee from './components/pages/NewEmployee/NewEmployee';
import './App.scss';

const App = () => {
  const [departments, setDepartments] = useState([
    'pénzügyi osztály',
    'személyügy',
    'igazgatóság',
    'szakszervezet',
  ]);

  const [employees, setEmployees] = useState([]);

  // only run once the first time this component is rendered
  useEffect(() => {
    if (localStorage.getItem('storedEmployees')) {
      setEmployees(JSON.parse(localStorage.getItem('storedEmployees')));
    }
  }, [employees]);

  // run every time our employees state changes
  useEffect(() => {
    localStorage.setItem('storedEmployees', JSON.stringify(employees));
  }, [employees]);

  // const storedEmployees = localStorage.getItem('storedEmployees')
  //   ? JSON.parse(localStorage.getItem('storedEmployees'))
  //   : [];

  // console.log('storedEmployees', storedEmployees);
  console.log(employees);

  const [test, setTest] = useState('');
  // setTest('test');
  // useEffect(() => {
  //   try {
  //     const data = localStorage.getItem('employees');

  //     if (data) {
  //       setEmployees(JSON.parse(data));
  //     } else {
  //       console.log('no data found');
  //       const dummy = {
  //         employeeId: '',
  //         familyName: '',
  //         givenName: '',
  //         birthdate: '',
  //         daysOffLimit: '',
  //         daysOffRemained: '',
  //         department: '',
  //       };
  //       setEmployees(JSON.parse(dummy));
  //     }
  //   } catch (error) {
  //     console.log('error');
  //     console.error(error);
  //   }
  // }, []);

  // useEffect(() => {
  //   localStorage.setItem('employees', JSON.stringify(employees));
  // });

  // const [employees, setEmployees] = useState(storedEmployees);

  // console.log('employees', typeof employees);
  // console.log('employees', employees);

  return (
    <div className='App'>
      <Navbar />
      <Routes>
        <Route
          path='/departments'
          element={<Departments departments={departments} />}
        />
        <Route
          path='/departments/new'
          element={<NewDepartment departments={departments} />}
        />
        <Route
          path='/employees'
          element={
            <Employees
              employees={employees}
              setEmployees={setEmployees}
              test={test}
              setTest={setTest}
            />
          }
        />
        <Route
          path='/employees/new'
          element={
            <NewEmployee
              departments={departments}
              employees={employees}
              setEmployees={setEmployees}
            />
          }
        />
        <Route
          path='/employees/edit'
          element={
            <EditEmployee
              departments={departments}
              employees={employees}
              setEmployees={setEmployees}
            />
          }
        />
        <Route path='/datalist' element={<Datalist />} />
      </Routes>
    </div>
  );
};

export default App;
