import React, { useState, useRef, useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import InputFieldSet from '../../common/InputFieldSet/InputFieldSet';
import { isFieldEmpty, isIdFormatValid } from '../../../utils/validators';
import {
  isFormValid,
  handleInputChange,
  handleInputBlur,
} from '../../../utils/form-validation';

const Employees = ({ employees }) => {
  const [formData, setFormData] = useState({
    employee: '',
  });

  const [namesOfEmployees, setNamesOfEmployees] = useState([]);
  const [idsOfEmployees, setIdsOfEmployees] = useState([]);

  // runs every time our employees state changes

  useEffect(() => {
    const fullNames = [];
    const ids = [];
    for (let i = 0; i < employees.length; i++) {
      fullNames.push(employees[i].familyName + ' ' + employees[i].givenName);
      ids.push(employees[i].employeeId);
      setIdsOfEmployees(ids);
      setNamesOfEmployees(fullNames);
    }
  }, [employees]);

  // let arrayOfNames = [];

  // employees.map(employee => {
  //   arrayOfNames.push(employee.familyName);
  //   return arrayOfNames;
  // });

  // console.log(arrayOfNames);
  // setNamesOfEmployees(arrayOfNames);
  const [alert, setAlert] = useState(null);
  const [formWasValidated, setFormWasValidated] = useState(false);

  const references = {
    employee: useRef(),
  };

  const [formErrors, setFormErrors] = useState({
    employee: '',
  });

  const messageTypes = Object.freeze({
    success: `Az adatok sikeresen mentésre kerültek`,
    fail: `Az adatok mentése sikertelen`,
  });

  const formErrorTypes = Object.freeze({
    required: `A mező kitöltése kötelező`,
  });

  const validators = {
    employee: {
      required: isFieldEmpty,
    },
  };

  return (
    <div className='container-sm mt-5'>
      <h1>Dolgozók</h1>
      <NavLink exact to='/employees/new'>
        <button type='button' className='btn btn-dark mt-5'>
          Létrehozás
        </button>{' '}
      </NavLink>

      <InputFieldSet
        name='employee'
        value={formData.employee}
        type='select'
        employeeOptions={employees}
        onChange={e => {
          handleInputChange(e, formData, setFormData);
        }}
        onBlur={e => {
          handleInputBlur(
            e,
            formData,
            setFormErrors,
            validators,
            references,
            formErrorTypes
          );
        }}
        reference={references.employee}
        error={formErrors.employee}
        required
      />
      {/* <select class='form-select' aria-label='Default select example'>
        <option selected>Open this select menu</option>
        {employees.map(employee => (
          <option value={employee.familyName} key={employee.familyName}>
            {employee.familyName} {employee.givenName}
          </option>
        ))}
      </select> */}

      <div className='container-l mt-5'>
        {/* <NavLink to={`/employees/edit/${employeeId}`}>
          <button type='button' className='btn btn-warning mt-5'>
            Módosítás
          </button>
        </NavLink> */}
        <button type='button' className='btn btn-danger mt-5'>
          Törlés
        </button>{' '}
      </div>
    </div>
  );
};

export default Employees;
