import React, { useState, useRef } from 'react';
import { NavLink } from 'react-router-dom';
import InputFieldSet from '../../common/InputFieldSet/InputFieldSet';
import { isFieldEmpty } from '../../../utils/validators';
import {
  isFormValid,
  handleInputChange,
  handleInputBlur,
} from '../../../utils/form-validation';
import { useNavigate } from 'react-router-dom';

const Departments = ({ department, setDepartment, departments }) => {
  let navigate = useNavigate();

  // const [departments, setDepartments] = useState([
  //   'pénzügyi osztály',
  //   'személyügy',
  //   'igazgatóság',
  //   'szakszervezet',
  // ]);

  console.log(departments);

  const [formData, setFormData] = useState({
    departmentId: '',
  });

  const [alert, setAlert] = useState(null);
  const [formWasValidated, setFormWasValidated] = useState(false);

  const references = {
    departmentId: useRef(),
  };

  const [formErrors, setFormErrors] = useState({
    departmentId: '',
  });

  const messageTypes = Object.freeze({
    success: `Az adatok sikeresen mentésre kerültek`,
    fail: `Az adatok mentése sikertelen`,
  });

  const formErrorTypes = Object.freeze({
    required: `A mező kitöltése kötelező`,
  });

  const validators = {
    departmentId: {
      required: isFieldEmpty,
    },
  };

  const handleModifyDepartment = e => {
    e.preventDefault();
    setAlert(null);
    setFormErrors({
      departmentId: '',
    });
    setFormWasValidated(false);
    const isValid = isFormValid(
      formData,
      setFormErrors,
      validators,
      references,
      formErrorTypes
    );
    if (isValid) {
      let foundDepartment = departments.filter(
        department => department.departmentId === formData.departmentId
      )[0];
      setDepartment(foundDepartment);
      console.log('department', department);
      navigate(`/departments/edit/${formData.departmentId}`);
    } else {
      console.log('no input');
      setFormWasValidated(false);
    }
  };

  return (
    <div className='container-sm mt-5'>
      <h1>Szervezeti egységek</h1>
      <NavLink to='/departments/new'>
        <button type='button' className='btn btn-dark mt-5'>
          Létrehozás
        </button>{' '}
      </NavLink>
      <form
        noValidate
        onSubmit={handleModifyDepartment}
        className={`needs-validation ${
          formWasValidated ? 'was-validated' : ''
        }`}
      >
        <InputFieldSet
          name='departmentId'
          value={formData.departmentId}
          type='select'
          departmentOptions={departments}
          onChange={e => {
            handleInputChange(e, formData, setFormData);
          }}
          onBlur={e => {
            handleInputBlur(
              e,
              formData,
              setFormErrors,
              validators,
              references,
              formErrorTypes
            );
          }}
          reference={references.departmentId}
          error={formErrors.departmentId}
          required
        />
        <div className='container-l mt-5'>
          <button type='submit' className='btn btn-warning mt-5'>
            Módosítás
          </button>
          <button type='button' className='btn btn-danger mt-5'>
            Törlés
          </button>
        </div>
      </form>
    </div>
  );
};

export default Departments;
