import React, { useState, useRef, useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import InputFieldSet from '../../common/InputFieldSet/InputFieldSet';
import { isFieldEmpty } from '../../../utils/validators';
import {
  isFormValid,
  handleInputChange,
  handleInputBlur,
} from '../../../utils/form-validation';

const Employees = ({ employees }) => {
  const [formData, setFormData] = useState({
    employeeId: '',
  });
  console.log('formData', formData);
  console.log('employees', employees);

 const [selectedEmployee, setSelectedEmployee] = useState()

  const [alert, setAlert] = useState(null);
  const [formWasValidated, setFormWasValidated] = useState(false);

  const references = {
    employeeId: useRef(),
  };

  const [formErrors, setFormErrors] = useState({
    employeeId: '',
  });

  const messageTypes = Object.freeze({
    success: `Az adatok sikeresen mentésre kerültek`,
    fail: `Az adatok mentése sikertelen`,
  });

  const formErrorTypes = Object.freeze({
    required: `A mező kitöltése kötelező`,
  });

  const validators = {
    employeeId: {
      required: isFieldEmpty,
    },
  };

  const handleSubmit = e => {
    e.preventDefault();
    setAlert(null);
    setFormErrors({
      employee: '',
    });
    setFormWasValidated(false);
    const isValid = isFormValid(
      formData,
      setFormErrors,
      validators,
      references,
      formErrorTypes
    );
    if (isValid) {
      console.log('isValid');
      console.log(formData.employeeId);
    } else {
      console.log('false');
      // setFormWasValidated(false);
    }
  };

  // const handleModifyEmployee = employees => {
  //   console.log(employees);
  //   console.log(formData.employeeId);

  //   let employeeToEdit = employees.filter(
  //     employee => employee.employeeId === formData.employeeId
  //   );
  //   console.log(employeeToEdit);
  // };


  return (
    <div className='container-sm mt-5'>
      <h1>Dolgozók</h1>
      <NavLink exact to='/employees/new'>
        <button type='button' className='btn btn-dark mt-5'>
          Létrehozás
        </button>{' '}
      </NavLink>
      <form
        noValidate
        onSubmit={handleSubmit}
        // className={`needs-validation ${formWasValidated && 'was-validated'}`}

        className={`needs-validation ${
          formWasValidated ? 'was-validated' : ''
        }`}
      >
        <InputFieldSet
          name='employeeId'
          value={formData.employeeId}
          type='select'
          employeeOptions={employees}
          onChange={e => {
            handleInputChange(e, formData, setFormData);
          }}
          onBlur={e => {
            handleInputBlur(
              e,
              formData,
              setFormErrors,
              validators,
              references,
              formErrorTypes
            );
          }}
          reference={references.employeeId}
          error={formErrors.employeeId}
          required
        />
        <div className='container-l mt-5'>
          {/* <NavLink to={`/employees/edit/${formData.employeeId}`}> */}
          <button
            type='submit'
            className='btn btn-warning mt-5'
            // onClick={() => handleModifyEmployee(employees)}
          >
            Módosítás
          </button>
          {/* </NavLink> */}
          <button type='button' className='btn btn-danger mt-5'>
            Törlés
          </button>
        </div>
      </form>
    </div>
  );
};

export default Employees;
