import EmployeeForm from '../../common/EmployeeForm/EmployeeForm';

const NewEmployee = () => {
  return (
    <div className='container-sm mt-5'>
      <h1>Új dolgozó hozzáadása</h1>
      <form
        noValidate
        onSubmit={handleSubmit}
        // className={`needs-validation ${formWasValidated && 'was-validated'}`}
        className={`needs-validation ${
          formWasValidated ? 'was-validated' : ''
        }`}
      >
        <InputFieldSet
          name='employeeId'
          value={formData.employeeId}
          labelText='Azonosító *'
          type='text'
          placeholder='pl: ab1234'
          onChange={e => {
            handleInputChange(e, formData, setFormData);
          }}
          onBlur={e => {
            handleInputBlur(
              e,
              formData,
              setFormErrors,
              validators,
              references,
              formErrorTypes
            );
          }}
          reference={references.employeeId}
          error={formErrors.employeeId}
          required
        />
        <InputFieldSet
          name='familyName'
          value={formData.familyName}
          labelText='Családi név *'
          type='text'
          onChange={e => {
            handleInputChange(e, formData, setFormData);
          }}
          onBlur={e => {
            handleInputBlur(
              e,
              formData,
              setFormErrors,
              validators,
              references,
              formErrorTypes
            );
          }}
          reference={references.familyName}
          error={formErrors.familyName}
          required
        />
        <InputFieldSet
          name='givenName'
          value={formData.givenName}
          labelText='Utónév *'
          type='text'
          onChange={e => {
            handleInputChange(e, formData, setFormData);
          }}
          onBlur={e => {
            handleInputBlur(
              e,
              formData,
              setFormErrors,
              validators,
              references,
              formErrorTypes
            );
          }}
          reference={references.givenName}
          error={formErrors.givenName}
          required
        />
        <InputFieldSet
          name='birthdate'
          value={formData.birthdate}
          labelText='Születési dátum *'
          type='date'
          onChange={e => {
            handleInputChange(e, formData, setFormData);
          }}
          onBlur={e => {
            handleInputBlur(
              e,
              formData,
              setFormErrors,
              validators,
              references,
              formErrorTypes
            );
          }}
          reference={references.birthdate}
          error={formErrors.birthdate}
          required
        />
        <InputFieldSet
          name='daysOffLimit'
          value={formData.daysOffLimit}
          labelText='Összes szabadnap *'
          type='number'
          onChange={e => {
            handleInputChange(e, formData, setFormData);
          }}
          onBlur={e => {
            handleInputBlur(
              e,
              formData,
              setFormErrors,
              validators,
              references,
              formErrorTypes
            );
          }}
          reference={references.daysOffLimit}
          error={formErrors.daysOffLimit}
          required
        />
        <InputFieldSet
          name='daysOffRemained'
          value={formData.daysOffRemained}
          labelText='Felhasználható szabadnap *'
          type='number'
          onChange={e => {
            handleInputChange(e, formData, setFormData);
          }}
          onBlur={e => {
            handleInputBlur(
              e,
              formData,
              setFormErrors,
              validators,
              references,
              formErrorTypes
            );
          }}
          reference={references.daysOffRemained}
          error={formErrors.daysOffRemained}
          required
        />
        <InputFieldSet
          name='department'
          value={formData.department}
          labelText='Foglalkoztató *'
          type='select'
          options={departments}
          onChange={e => {
            handleInputChange(e, formData, setFormData);
          }}
          onBlur={e => {
            handleInputBlur(
              e,
              formData,
              setFormErrors,
              validators,
              references,
              formErrorTypes
            );
          }}
          reference={references.department}
          error={formErrors.department}
          required
        />
        <div className='container-l'>
          <button type='submit' className='btn btn-warning mt-5'>
            Mentés
          </button>{' '}
          <NavLink exact to='/employees' activeClassName='activeClicked'>
            <button type='button' className='btn btn-dark mt-5'>
              Mégsem
            </button>{' '}
          </NavLink>
        </div>
      </form>
      <div className='alert-cont'>
        {alert && (
          <p className={`alert alert-${alert.alertType}`}>{alert.message}</p>
        )}
      </div>
    </div>
  );
};

export default NewEmployee;
