import React, { useState } from 'react';
import { NavLink } from 'react-router-dom';

const Departments = () => {
  const [departments, setDepartments] = useState([
    'pénzügyi osztály',
    'személyügy',
    'igazgatóság',
    'szakszervezet',
  ]);

  return (
    <div className='container-sm mt-5'>
      <h1>Szervezeti egységek</h1>
      <NavLink exact to='/newDepartment'>
        <button type='button' class='btn btn-dark mt-5'>
          Létrehozás
        </button>{' '}
      </NavLink>

      <select className='form-select mt-5' aria-label='Default select example'>
        <option selected>Válassz szervezeti egységet</option>
        {departments.map(department => (
          <option key={department} value={department}>
            {department}
          </option>
        ))}
      </select>

      <div className='container-l mt-5'>
        <button type='button' class='btn btn-warning mt-5'>
          Módosítás
        </button>{' '}
        <button type='button' class='btn btn-danger mt-5'>
          Törlés
        </button>{' '}
      </div>
    </div>
  );
};

export default Departments;
