import React, { useState, useEffect } from 'react';
import { Routes, Route } from 'react-router-dom';
import Navbar from './components/common/Navbar/Navbar';
import Departments from './components/pages/Departments/Departments';
import Employees from './components/pages/Employees/Employees';
import Datalist from './components/pages/Datalist/Datalist';
import NewDepartment from './components/pages/NewDepartment/NewDepartment';
import NewEmployee from './components/pages/NewEmployee/NewEmployee';
import EditEmployee from './components/pages/NewEmployee/NewEmployee';
import './App.scss';

const App = () => {
  const [departments, setDepartments] = useState([
    'pénzügyi osztály',
    'személyügy',
    'igazgatóság',
    'szakszervezet',
  ]);

  const [employees, setEmployees] = useState(() => {
    const storedEmployees = localStorage.getItem('employees');
    const initialValue = JSON.parse(storedEmployees);
    return initialValue || [];
  });

  // const [employees, setEmployees] = useState([
  //   {
  //     familyName: 'Kiss',
  //     givenName: 'Laszlo',
  //   },
  //   {
  //     familyName: 'Nagy',
  //     givenName: 'Bela',
  //   },
  //   {
  //     familyName: 'Hosszu',
  //     givenName: 'Piroska',
  //   },
  // ]);
  console.log('employees', typeof employees);

  // useEffect(() => {
  //   const storedEmployees = localStorage.getItem('employees');
  //   if (!storedEmployees) {
  //     console.log('no employees found in storage');
  //     setEmployees([]);
  //   } else {
  //     console.log('employees have been found in storage');
  //     setEmployees(JSON.parse(storedEmployees));
  //   }
  // }, []);

  return (
    <div className='App'>
      <Navbar />
      <Routes>
        <Route
          path='departments'
          element={<Departments departments={departments} />}
        />
        <Route path='newDepartment' element={<NewDepartment />} />
        <Route path='employees' element={<Employees employees={employees} />} />
        <Route
          path='employees/new'
          element={
            <NewEmployee
              departments={departments}
              employees={employees}
              setEmployees={setEmployees}
            />
          }
        />
        <Route
          path='employees/edit'
          element={
            <EditEmployee
              departments={departments}
              employees={employees}
              setEmployees={setEmployees}
            />
          }
        />
        <Route path='datalist' element={<Datalist />} />
      </Routes>
    </div>
  );
};

export default App;
