import React, { useState, useRef } from 'react';
import { NavLink } from 'react-router-dom';
import InputFieldSet from '../../common/InputFieldSet/InputFieldSet';
import { isFieldEmpty, isIdFormatValid } from '../../../utils/validators';
import {
  isFormValid,
  handleInputChange,
  handleInputBlur,
} from '../../../utils/form-validation';

const Employees = ({ employee }) => {
  const [formData, setFormData] = useState({
    employee: '',
  });

  const [employees, setEmployees] = useState([
    'Kiis Béla',
    'Gipsz Jakab',
    'Nagy Miska',
    'Hortháth Panna',
  ]);

  const [alert, setAlert] = useState(null);
  const [formWasValidated, setFormWasValidated] = useState(false);

  const references = {
    employee: useRef(),
  };

  const [formErrors, setFormErrors] = useState({
    employee: '',
  });

  const messageTypes = Object.freeze({
    success: `Az adatok sikeresen mentésre kerültek`,
    fail: `Az adatok mentése sikertelen`,
  });

  const formErrorTypes = Object.freeze({
    required: `A mező kitöltése kötelező`,
  });

  const validators = {
    employee: {
      required: isFieldEmpty,
    },
  };

  return (
    <div className='container-sm mt-5'>
      <h1>Dolgozók</h1>
      <NavLink exact to='/newEmployee' activeClassName='activeClicked'>
        <button type='button' class='btn btn-dark mt-5'>
          Létrehozás
        </button>{' '}
      </NavLink>

      <InputFieldSet
        name='employee'
        value={formData.employee}
        labelText='Foglalkoztató *'
        type='select'
        options={employees}
        onChange={e => {
          handleInputChange(e, formData, setFormData);
        }}
        onBlur={e => {
          handleInputBlur(
            e,
            formData,
            setFormErrors,
            validators,
            references,
            formErrorTypes
          );
        }}
        reference={references.employee}
        error={formErrors.employee}
        required
      />

      <div className='container-l mt-5'>
        <NavLink to={`/employees/edit/${employee.id}`}>
          <button type='button' class='btn btn-warning mt-5'>
            Módosítás
          </button>
        </NavLink>
        <button type='button' class='btn btn-danger mt-5'>
          Törlés
        </button>{' '}
      </div>
    </div>
  );
};

export default Employees;
