import React, { useState, useRef, useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import InputFieldSet from '../../common/InputFieldSet/InputFieldSet';
import { isFieldEmpty, isIdFormatValid } from '../../../utils/validators';
import {
  isFormValid,
  handleInputChange,
  handleInputBlur,
} from '../../../utils/form-validation';

const Employees = () => {
  const [formData, setFormData] = useState({
    employee: '',
  });

  const [employees, setEmployees] = useState();
  const [namesOfEmployees, setNamesOfEmployees] = useState();

  useEffect(() => {
    const storedEmployees = localStorage.getItem('employees');
    if (!storedEmployees) {
      console.log('no employees found in storage');
      setEmployees([]);
    } else {
      console.log('employees have been found in storage');
      setEmployees(JSON.parse(storedEmployees));

      // for (let i = 0; i < employees.length; i++) {
      //   const familyName = employees[i].familyName;
      //   const givenName = employees[i].givenName;
      //   const fullName = familyName + ' ' + givenName;
      //   let fullNames = [];
      //   fullNames.push(fullName);
      //   setNamesOfEmployees(fullName);
      // }
    }
  }, []);

  console.log('employees', typeof employees);
  // console.log('namesOfEmployees', typeof namesOfEmployees);

  const [alert, setAlert] = useState(null);
  const [formWasValidated, setFormWasValidated] = useState(false);

  const references = {
    employee: useRef(),
  };

  const [formErrors, setFormErrors] = useState({
    employee: '',
  });

  const messageTypes = Object.freeze({
    success: `Az adatok sikeresen mentésre kerültek`,
    fail: `Az adatok mentése sikertelen`,
  });

  const formErrorTypes = Object.freeze({
    required: `A mező kitöltése kötelező`,
  });

  const validators = {
    employee: {
      required: isFieldEmpty,
    },
  };

  return (
    <div className='container-sm mt-5'>
      <h1>Dolgozók</h1>
      <NavLink exact to='/employees/new'>
        <button type='button' className='btn btn-dark mt-5'>
          Létrehozás
        </button>{' '}
      </NavLink>

      {/* <InputFieldSet
        name='employee'
        value={formData.employee}
        labelText='Foglalkoztató *'
        type='select'
        options={namesOfEmployees}
        onChange={e => {
          handleInputChange(e, formData, setFormData);
        }}
        onBlur={e => {
          handleInputBlur(
            e,
            formData,
            setFormErrors,
            validators,
            references,
            formErrorTypes
          );
        }}
        reference={references.employee}
        error={formErrors.employee}
        required
      /> */}

      <div className='container-l mt-5'>
        <NavLink to={`/employees/edit`}>
          <button type='button' className='btn btn-warning mt-5'>
            Módosítás
          </button>
        </NavLink>
        <button type='button' className='btn btn-danger mt-5'>
          Törlés
        </button>{' '}
      </div>
    </div>
  );
};

export default Employees;
