import React, { useState, useRef } from 'react';
import { NavLink } from 'react-router-dom';
import InputField from '../../common/InputField/InputField';
import {
  isFieldEmpty,
  isEmailInvalid,
  // isDepartmentIdValid,
} from '../../../utils/validators';
import {
  isFormValid,
  handleInputChange,
  handleInputBlur,
} from '../../../utils/form-validation';

const NewDepartment = () => {
  const [formData, setFormData] = useState({
    departmentId: '',
    departmentName: '',
  });

  const [alert, setAlert] = useState(null);
  const [formWasValidated, setFormWasValidated] = useState(false);

  const references = {
    email: useRef(),
    departmentId: useRef(),
  };

  const formErrorTypes = Object.freeze({
    required: `A mez≈ë kit√∂lt√©se k√∂telez≈ë`,
    departmentIdLength: `Az azonos√≠t√≥ 6 karakter hossz√∫ kell legyen`,
    departmentIdFormat: `Az azonos√≠t√≥ nem megfelel≈ë form√°tum√∫ (2 kis - vagy nagybet≈± √©s 4 sz√°m)`,
  });

  const [formErrors, setFormErrors] = useState({
    email: '',
    departmentId: '',
  });

  const messageTypes = Object.freeze({
    failCaptcha: `Please prove me that you're not a robot ü§ñ`,
  });

  const validators = {
    email: {
      required: isFieldEmpty,
      validEmail: isEmailInvalid,
    },
    departmentId: {
      required: isFieldEmpty,
      // departmentIsdLength: isdepartmentIdValid,
    },
  };

  return (
    <div className='container-sm mt-5'>
      <h1>√öj szervezeti egys√©g hozz√°ad√°sa</h1>
      <NavLink exact to='/departments' activeClassName='activeClicked'>
        <button type='button' class='btn btn-dark mt-5'>
          Vissza
        </button>{' '}
      </NavLink>
      <form
        noValidate
        // onSubmit={handleLogin}
        className={`needs-validation ${formWasValidated && 'was-validated'}`}
      >
        <InputField
          name='departmentId'
          value={formData.departmentId}
          labelText='Azonos√≠t√≥ *'
          onChange={e => {
            handleInputChange(e, formData, setFormData);
          }}
          onBlur={e => {
            handleInputBlur(
              e,
              formData,
              setFormErrors,
              validators,
              references,
              formErrorTypes
            );
          }}
          reference={references.departmentId}
          error={formErrors.departmentId}
          required
        />
      </form>
    </div>
  );
};

export default NewDepartment;
