import React, { useState, useRef, useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import InputFieldSet from '../InputFieldSet/InputFieldSet';
import { isFieldEmpty, isIdFormatValid } from '../../../utils/validators';
import {
  isFormValid,
  handleInputChange,
  handleInputBlur,
} from '../../../utils/form-validation';

const EmployeeForm = ({
  type,
  employee,
  employees,
  setEmployees,
  departments
}) => {

  // useEffect(() => {
  //   localStorage.setItem('employees', JSON.stringify(employees));
  // });

  const [formData, setFormData] = useState(
    type === 'edit'
      ? employee
      : {
          employeeId: '',
          familyName: '',
          givenName: '',
          birthdate: '',
          daysOffLimit: '',
          daysOffRemained: '',
          department: '',
        }
  );

function addEntry(data) {
    var existingEntries = JSON.parse(localStorage.getItem("employees"));
    if(existingEntries == null) existingEntries = [];

    localStorage.setItem("entry", JSON.stringify(data));
    existingEntries.push(data);
    localStorage.setItem("allEntries", JSON.stringify(existingEntries));
};

  const [alert, setAlert] = useState(null);
  const [formWasValidated, setFormWasValidated] = useState(false);

  const references = {
    employeeId: useRef(),
    familyName: useRef(),
    givenName: useRef(),
    birthdate: useRef(),
    daysOffLimit: useRef(),
    daysOffRemained: useRef(),
    department: useRef(),
  };

  const formErrorTypes = Object.freeze({
    required: `A mező kitöltése kötelező`,
    employeeIdFormat: `Az azonosító nem megfelelő formátumú (2 kis - vagy nagybetű és 4 szám)`,
  });

  const [formErrors, setFormErrors] = useState({
    employeeId: '',
    familyName: '',
    givenName: '',
    birthdate: '',
    daysOffLimit: '',
    daysOffRemained: '',
    department: '',
  });

  const messageTypes = Object.freeze({
    success: `Az adatok sikeresen mentésre kerültek`,
    fail: `Az adatok mentése sikertelen`,
  });

  const validators = {
    employeeId: {
      required: isFieldEmpty,
      employeeIdFormat: isIdFormatValid,
    },
    familyName: {
      required: isFieldEmpty,
    },
    givenName: {
      required: isFieldEmpty,
    },
    birthdate: {
      required: isFieldEmpty,
    },
    daysOffLimit: {
      required: isFieldEmpty,
    },
    daysOffRemained: {
      required: isFieldEmpty,
    },
    department: {
      required: isFieldEmpty,
    },
  };

  const handleSubmit = e => {
    e.preventDefault();

    setAlert('');
    setFormErrors({
      employeeId: '',
      familyName: '',
      givenName: '',
      birthdate: '',
      daysOffLimit: '',
      daysOffRemained: '',
      department: '',
    });
    setFormWasValidated(false);
    const isValid = isFormValid(
      formData,
      setFormErrors,
      validators,
      references,
      formErrorTypes
    );

    if (isValid) {
      if (type === 'new') {
         addEntry(formData)
        console.log('employees', employees);

        setFormData({
          employeeId: '',
          familyName: '',
          givenName: '',
          birthdate: '',
          daysOffLimit: '',
          daysOffRemained: '',
          department: '',
        });
        setAlert(messageTypes.success);
      }
      if (type === 'edit') {
        console.log(formData);
        console.log('data valid');
        setFormData({
          employeeId: '',
          familyName: '',
          givenName: '',
          birthdate: '',
          daysOffLimit: '',
          daysOffRemained: '',
          department: '',
        });
        setAlert(messageTypes.success);
      }
    } else {
      setFormWasValidated(true);
    }
  };
  return (
    <div className='container-sm mt-5'>
      <form
        noValidate
        onSubmit={handleSubmit}
        // className={`needs-validation ${formWasValidated && 'was-validated'}`}
        className={`needs-validation ${
          formWasValidated ? 'was-validated' : ''
        }`}
      >
        <InputFieldSet
          name='employeeId'
          value={formData.employeeId}
          labelText='Azonosító *'
          type='text'
          placeholder='pl: ab1234'
          onChange={e => {
            handleInputChange(e, formData, setFormData);
          }}
          onBlur={e => {
            handleInputBlur(
              e,
              formData,
              setFormErrors,
              validators,
              references,
              formErrorTypes
            );
          }}
          reference={references.employeeId}
          error={formErrors.employeeId}
          required
        />
        <InputFieldSet
          name='familyName'
          value={formData.familyName}
          labelText='Családi név *'
          type='text'
          onChange={e => {
            handleInputChange(e, formData, setFormData);
          }}
          onBlur={e => {
            handleInputBlur(
              e,
              formData,
              setFormErrors,
              validators,
              references,
              formErrorTypes
            );
          }}
          reference={references.familyName}
          error={formErrors.familyName}
          required
        />
        <InputFieldSet
          name='givenName'
          value={formData.givenName}
          labelText='Utónév *'
          type='text'
          onChange={e => {
            handleInputChange(e, formData, setFormData);
          }}
          onBlur={e => {
            handleInputBlur(
              e,
              formData,
              setFormErrors,
              validators,
              references,
              formErrorTypes
            );
          }}
          reference={references.givenName}
          error={formErrors.givenName}
          required
        />
        <InputFieldSet
          name='birthdate'
          value={formData.birthdate}
          labelText='Születési dátum *'
          type='date'
          onChange={e => {
            handleInputChange(e, formData, setFormData);
          }}
          onBlur={e => {
            handleInputBlur(
              e,
              formData,
              setFormErrors,
              validators,
              references,
              formErrorTypes
            );
          }}
          reference={references.birthdate}
          error={formErrors.birthdate}
          required
        />
        <InputFieldSet
          name='daysOffLimit'
          value={formData.daysOffLimit}
          labelText='Összes szabadnap *'
          type='number'
          onChange={e => {
            handleInputChange(e, formData, setFormData);
          }}
          onBlur={e => {
            handleInputBlur(
              e,
              formData,
              setFormErrors,
              validators,
              references,
              formErrorTypes
            );
          }}
          reference={references.daysOffLimit}
          error={formErrors.daysOffLimit}
          required
        />
        <InputFieldSet
          name='daysOffRemained'
          value={formData.daysOffRemained}
          labelText='Felhasználható szabadnap *'
          type='number'
          onChange={e => {
            handleInputChange(e, formData, setFormData);
          }}
          onBlur={e => {
            handleInputBlur(
              e,
              formData,
              setFormErrors,
              validators,
              references,
              formErrorTypes
            );
          }}
          reference={references.daysOffRemained}
          error={formErrors.daysOffRemained}
          required
        />
        <InputFieldSet
          name='department'
          value={formData.department}
          labelText='Foglalkoztató *'
          type='select'
          options={departments}
          onChange={e => {
            handleInputChange(e, formData, setFormData);
          }}
          onBlur={e => {
            handleInputBlur(
              e,
              formData,
              setFormErrors,
              validators,
              references,
              formErrorTypes
            );
          }}
          reference={references.department}
          error={formErrors.department}
          required
        />
        <div className='container-l'>
          <button type='submit' className='btn btn-warning mt-5'>
            Mentés
          </button>{' '}
          <NavLink exact to='/employees'>
            <button type='button' className='btn btn-dark mt-5'>
              Mégsem
            </button>{' '}
          </NavLink>
        </div>
      </form>
      <div className='alert-cont'>
        {alert && (
          <p className={`alert alert-${alert.alertType}`}>{alert.message}</p>
        )}
      </div>
    </div>
  );
};

export default EmployeeForm;
