import React, { useState, useRef, useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import InputFieldSet from '../InputFieldSet/InputFieldSet';
import { isFieldEmpty, isIdFormatValid } from '../../../utils/validators';
import {
  isFormValid,
  handleInputChange,
  handleInputBlur,
} from '../../../utils/form-validation';

const DepartmentForm = ({ type, department, departments }) => {
  const [formData, setFormData] = useState(
    type === 'edit'
      ? department
      : {
          departmentId: '',
          departmentName: '',
        }
  );

  const [alert, setAlert] = useState(null);
  const [formWasValidated, setFormWasValidated] = useState(false);

  const references = {
    departmentId: useRef(),
    departmentName: useRef(),
  };

  const formErrorTypes = Object.freeze({
    required: `A mező kitöltése kötelező`,
    departmentIdFormat: `Az azonosító nem megfelelő formátumú (2 kis - vagy nagybetű és 4 szám)`,
  });

  const [formErrors, setFormErrors] = useState({
    departmentId: '',
    departmentName: '',
  });

  const messageTypes = Object.freeze({
    success: `Az adatok sikeresen mentésre kerültek`,
    fail: `Az adatok mentése sikertelen`,
  });

  const validators = {
    departmentId: {
      required: isFieldEmpty,
      departmentIdFormat: isIdFormatValid,
    },
    departmentName: {
      required: isFieldEmpty,
    },
  };

  const handleSubmit = e => {
    e.preventDefault();

    setAlert('');
    setFormErrors({
      departmentId: '',
      departmentName: '',
    });
    setFormWasValidated(false);
    const isValid = isFormValid(
      formData,
      setFormErrors,
      validators,
      references,
      formErrorTypes
    );

    if (isValid) {
      if (type === 'new') {
        //   setEmployees(prev => prev.concat(formData));
        //   console.log('employees', employees);

        setFormData('');
        setAlert(messageTypes.success);
      }
      if (type === 'edit') {
        console.log(formData);
        console.log('data valid');

        setAlert(messageTypes.success);
      }
    } else {
      setFormWasValidated(true);
    }
  };
  return (
    <div className='container-sm mt-5'>
      <form
        noValidate
        onSubmit={handleSubmit}
        // className={`needs-validation ${formWasValidated && 'was-validated'}`}
        className={`needs-validation ${
          formWasValidated ? 'was-validated' : ''
        }`}
      >
        <InputFieldSet
          name='departmentId'
          value={formData.departmentId}
          labelText='Azonosító *'
          type='text'
          placeholder='pl: ab1234'
          onChange={e => {
            handleInputChange(e, formData, setFormData);
          }}
          onBlur={e => {
            handleInputBlur(
              e,
              formData,
              setFormErrors,
              validators,
              references,
              formErrorTypes
            );
          }}
          reference={references.departmentId}
          error={formErrors.departmentId}
          required
        />
        <InputFieldSet
          name='departmentName'
          value={formData.departmentName}
          labelText='Név *'
          type='text'
          onChange={e => {
            handleInputChange(e, formData, setFormData);
          }}
          onBlur={e => {
            handleInputBlur(
              e,
              formData,
              setFormErrors,
              validators,
              references,
              formErrorTypes
            );
          }}
          reference={references.departmentName}
          error={formErrors.departmentName}
          required
        />
        <div className='container-l'>
          <button type='submit' className='btn btn-warning mt-5'>
            Mentés
          </button>{' '}
          <NavLink to='/employees'>
            <button type='button' className='btn btn-dark mt-5'>
              Mégsem
            </button>{' '}
          </NavLink>
        </div>
      </form>
      <div className='alert-cont'>
        {alert && (
          <p className={`alert alert-${alert.alertType}`}>{alert.message}</p>
        )}
      </div>
    </div>
  );
};

export default DepartmentForm;
