import React, { useState, useRef, useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import InputFieldSet from '../../common/InputFieldSet/InputFieldSet';
import { isFieldEmpty, isIdFormatValid } from '../../../utils/validators';
import {
  isFormValid,
  handleInputChange,
  handleInputBlur,
} from '../../../utils/form-validation';

const Employees = ({ employees }) => {
  const [formData, setFormData] = useState({
    employeeId: '',
  });
  console.log(formData.employeeId);

  const handleModifyEmployee = employees => {
    let employeeToEdit = employees.filter(
      employee => employee.employeeId === formData.employeeId
    );
    console.log(employeeToEdit);
  };

  const [alert, setAlert] = useState(null);
  const [formWasValidated, setFormWasValidated] = useState(false);

  const references = {
    employee: useRef(),
  };

  const [formErrors, setFormErrors] = useState({
    employee: '',
  });

  const messageTypes = Object.freeze({
    success: `Az adatok sikeresen mentésre kerültek`,
    fail: `Az adatok mentése sikertelen`,
  });

  const formErrorTypes = Object.freeze({
    required: `A mező kitöltése kötelező`,
  });

  const validators = {
    employee: {
      required: isFieldEmpty,
    },
  };

  return (
    <div className='container-sm mt-5'>
      <h1>Dolgozók</h1>
      <NavLink exact to='/employees/new'>
        <button type='button' className='btn btn-dark mt-5'>
          Létrehozás
        </button>{' '}
      </NavLink>

      <InputFieldSet
        name='employee'
        value={formData.employee}
        type='select'
        employeeOptions={employees}
        onChange={e => {
          handleInputChange(e, formData, setFormData);
        }}
        onBlur={e => {
          handleInputBlur(
            e,
            formData,
            setFormErrors,
            validators,
            references,
            formErrorTypes
          );
        }}
        reference={references.employee}
        error={formErrors.employee}
        required
      />

      <div className='container-l mt-5'>
        {/* <NavLink to={`/employees/edit/${formData.employeeId}`}> */}
        <button
          type='button'
          className='btn btn-warning mt-5'
          onClick={() => handleModifyEmployee(employees)}
        >
          Módosítás
        </button>
        {/* </NavLink> */}
        <button type='button' className='btn btn-danger mt-5'>
          Törlés
        </button>
      </div>
    </div>
  );
};

export default Employees;
