import React, { useState } from 'react';

const Datalist = ({ employees }) => {
  const [nameSearch, setNameSearch] = useState([]);

  const onSearchChange = e => {
    const searchTerm = e.target.value;
    const namesFound = employees.filter(
      employee => employee.familyName === searchTerm
    );
    console.log('namesFound', namesFound);
    setNameSearch(namesFound);
  };

  console.log('nameSearch', nameSearch);
  return (
    <div className='container-sm mt-5'>
      <h1>Adatlista</h1>
      <table className='table table-striped table-bordered'>
        <thead>
          <tr>
            <th>#</th>
            <th scope='col'>Dolgozó azonosítója</th>
            <th scope='col'>Dolgozó teljes neve</th>
            <th scope='col'>Foglalkoztató egység</th>
          </tr>
          <tr>
            <th></th>
            <th scope='col'>
              <div className='mb-3'>
                <input
                  type='text'
                  className='form-control'
                  id='search'
                  onChange={onSearchChange}
                />
              </div>
            </th>
            <th scope='col'>
              <div className='mb-3'>
                <input
                  type='text'
                  className='form-control'
                  id='search'
                  onChange={onSearchChange}
                />
              </div>
            </th>
            <th scope='col'>
              <div className='mb-3'>
                <input
                  type='text'
                  className='form-control'
                  id='search'
                  onChange={onSearchChange}
                />
              </div>
            </th>
          </tr>
        </thead>
        <tbody>
          {nameSearch.length !== 0
            ? nameSearch.map((employee, index) => (
                <tr key={index}>
                  <td>{index + 1}</td>
                  <td>{employee.employeeId}</td>
                  <td>
                    {employee.familyName} {employee.givenName}
                  </td>
                  <td>{employee.department}</td>
                </tr>
              ))
            : employees.map((employee, index) => (
                <tr key={index}>
                  <td>{index + 1}</td>
                  <td>{employee.employeeId}</td>
                  <td>
                    {employee.familyName} {employee.givenName}
                  </td>
                  <td>{employee.department}</td>
                </tr>
              ))}
        </tbody>
      </table>
    </div>
  );
};

export default Datalist;
