import React, { useState, useRef } from 'react';
import { NavLink } from 'react-router-dom';
import InputField from '../../common/InputField/InputField';
import { isFieldEmpty, isIdFormatValid } from '../../../utils/validators';
import {
  isFormValid,
  handleInputChange,
  handleInputBlur,
} from '../../../utils/form-validation';

const NewEmployee = () => {
  const [formData, setFormData] = useState({
    employeeId: '',
    familyName: '',
    givenName: '',
  });

  const [alert, setAlert] = useState(null);
  const [formWasValidated, setFormWasValidated] = useState(false);

  const references = {
    employeeId: useRef(),
    familyName: useRef(),
    givenName: useRef(),
  };

  const formErrorTypes = Object.freeze({
    required: `A mez≈ë kit√∂lt√©se k√∂telez≈ë`,
    employeeIdFormat: `Az azonos√≠t√≥ nem megfelel≈ë form√°tum√∫ (2 kis - vagy nagybet≈± √©s 4 sz√°m)`,
  });

  const [formErrors, setFormErrors] = useState({
    employeeId: '',
    familyName: '',
    givenName: '',
  });

  const messageTypes = Object.freeze({
    failCaptcha: `Please prove me that you're not a robot ü§ñ`,
  });

  const validators = {
    employeeId: {
      required: isFieldEmpty,
      employeeIdFormat: isIdFormatValid,
    },
    familyName: {
      required: isFieldEmpty,
    },
    givenName: {
      required: isFieldEmpty,
    },
  };

  return (
    <div className='container-sm mt-5'>
      <h1>√öj dolgoz√≥ hozz√°ad√°sa</h1>
      <form
        noValidate
        // onSubmit={handleLogin}
        className={`needs-validation ${formWasValidated && 'was-validated'}`}
      >
        <InputField
          placeholder='pl: ab1234'
          name='employeeId'
          value={formData.employeeId}
          labelText='Azonos√≠t√≥ *'
          onChange={e => {
            handleInputChange(e, formData, setFormData);
          }}
          onBlur={e => {
            handleInputBlur(
              e,
              formData,
              setFormErrors,
              validators,
              references,
              formErrorTypes
            );
          }}
          reference={references.employeeId}
          error={formErrors.employeeId}
          required
        />
        <InputField
          name='familyName'
          value={formData.familyName}
          labelText='Csal√°di n√©v *'
          onChange={e => {
            handleInputChange(e, formData, setFormData);
          }}
          onBlur={e => {
            handleInputBlur(
              e,
              formData,
              setFormErrors,
              validators,
              references,
              formErrorTypes
            );
          }}
          reference={references.familyName}
          error={formErrors.familyName}
          required
        />
        <InputField
          name='givenName'
          value={formData.givenName}
          labelText='Ut√≥n√©v *'
          onChange={e => {
            handleInputChange(e, formData, setFormData);
          }}
          onBlur={e => {
            handleInputBlur(
              e,
              formData,
              setFormErrors,
              validators,
              references,
              formErrorTypes
            );
          }}
          reference={references.givenName}
          error={formErrors.givenName}
          required
        />
        <div className='container-l'>
          <button type='button' class='btn btn-warning mt-5'>
            Ment√©s
          </button>{' '}
          <NavLink exact to='/employees' activeClassName='activeClicked'>
            <button type='button' class='btn btn-dark mt-5'>
              M√©gsem
            </button>{' '}
          </NavLink>
        </div>
      </form>
    </div>
  );
};

export default NewEmployee;
