import React, { useState, useRef, useEffect } from 'react';
import { NavLink } from 'react-router-dom';
import InputFieldSet from '../../common/InputFieldSet/InputFieldSet';
import { isFieldEmpty } from '../../../utils/validators';
import {
  isFormValid,
  handleInputChange,
  handleInputBlur,
} from '../../../utils/form-validation';
import { useNavigate } from 'react-router-dom';

const Employees = ({ employees }) => {
  const navigate = useNavigate();

  const [formData, setFormData] = useState({
    employeeId: '',
  });

  const [selectedEmployee, setSelectedEmployee] = useState('');

  const [alert, setAlert] = useState(null);
  const [formWasValidated, setFormWasValidated] = useState(false);

  const references = {
    employeeId: useRef(),
  };

  const [formErrors, setFormErrors] = useState({
    employeeId: '',
  });

  const messageTypes = Object.freeze({
    success: `Az adatok sikeresen mentésre kerültek`,
    fail: `Az adatok mentése sikertelen`,
  });

  const formErrorTypes = Object.freeze({
    required: `A mező kitöltése kötelező`,
  });

  const validators = {
    employeeId: {
      required: isFieldEmpty,
    },
  };

  const handleModifyEmployee = e => {
    e.preventDefault();
    setAlert(null);
    setFormErrors({
      employeeId: '',
    });
    setFormWasValidated(false);
    const isValid = isFormValid(
      formData,
      setFormErrors,
      validators,
      references,
      formErrorTypes
    );
    if (isValid) {
      let foundEmployee = employees.filter(
        employee => employee.employeeId === formData.employeeId
      )[0];
      setSelectedEmployee(foundEmployee);
      console.log('selectedEmployee', selectedEmployee);
      navigate(`/employees/edit/${selectedEmployee.employeeId}`);
    } else {
      console.log('false');
      setFormWasValidated(false);
    }
  };

  // const handleModifyEmployee = id => {
  //   console.log(id);

  //   const employeeToEdit = employees.filter(
  //     employee => employee.employeeId === id
  //   );
  //   console.log('employeeToEdit', employeeToEdit);
  // };

  return (
    <div className='container-sm mt-5'>
      <h1>Dolgozók</h1>
      <NavLink exact to='/employees/new'>
        <button type='button' className='btn btn-dark mt-5'>
          Létrehozás
        </button>{' '}
      </NavLink>
      <form
        noValidate
        onSubmit={handleModifyEmployee}
        className={`needs-validation ${
          formWasValidated ? 'was-validated' : ''
        }`}
      >
        <InputFieldSet
          name='employeeId'
          value={formData.employeeId}
          type='select'
          employeeOptions={employees}
          onChange={e => {
            handleInputChange(e, formData, setFormData);
          }}
          onBlur={e => {
            handleInputBlur(
              e,
              formData,
              setFormErrors,
              validators,
              references,
              formErrorTypes
            );
          }}
          reference={references.employeeId}
          error={formErrors.employeeId}
          required
        />
        <div className='container-l mt-5'>
          <button type='submit' className='btn btn-warning mt-5'>
            Módosítás
          </button>
          <button type='button' className='btn btn-danger mt-5'>
            Törlés
          </button>
        </div>
      </form>
    </div>
  );
};

export default Employees;
