import React from 'react';

const InputField = ({
  type,
  name,
  value,
  onChange,
  onBlur,
  reference,
  error,
  labelText,
  placeholder,
  centerClass,
  passwordShown,
  setPasswordShown,
}) => {
  let inputField = (
    <div className={`${error && 'was-validated'}`}>
      <label className={`form-label mt-2 ${centerClass}`} htmlFor={name}>
        {labelText}
      </label>
      <input
        placeholder={placeholder}
        className={`form-control mt-2 ${centerClass}`}
        type={type}
        name={name}
        value={value}
        onChange={onChange}
        onBlur={onBlur}
        ref={reference}
      />
      <div className={`invalid-feedback mx-2 ${centerClass}`}>{error}</div>
    </div>
  );
};

export default InputField;
